void Foo([ValueRange(0, 100)] int value) {
  if (value == -1) { // Warning: Expression is always 'false'
    ...
  }
}

void Foo([NonNegativeValue] int value) {
  if (value == -1) { // Warning: Expression is always 'false'
    ...
  }
}

